pragma solidity ^0.4.24;

contract Mtama2 {

    //only contract owner should be able to mint tokens.
    address contractOwner;

    struct Deposit {
        address sender;
        address receiver;
        uint256 amount;
    }

    mapping (string => Deposit) deposits; //guid identifier generated from the front-end.
    
    //contract deployment event. Called only once
    event contractDeployed(address deployer, string message);
    //get notified when a deposit is done
    event escrowStarted(address depositor, address receiver, uint256 amount);
    //get notified when a verification is done
    event escrowReleased(address depositor, address receiver, uint256 amount);
    //get notified when money is sent back to payor
    event claimRefund(address depositor, uint256 amount);
    
    constructor() public {
        contractOwner = msg.sender;
        
        emit contractDeployed(contractOwner, "Mtama Escrow Contract Deployed");        
    }
    
    function hello() public view returns(uint) {
        return 100;
    }

    function startEscrow(string memory _guid, address _to) public payable  {
        require(_to != address(0), "Receiver Address Empty");
        require (deposits[_guid].sender == address(0), "StartEscrow: Escrow with the same id already exists");

        Deposit memory _dep = Deposit({
            sender: msg.sender,
            receiver: _to,
            amount: msg.value 
        });
        deposits[_guid] = _dep;
        
        emit escrowStarted(msg.sender, _to, msg.value);
    }

    function releaseEscrow(string memory _guid, bool _isRefund) public {
        require (contractOwner == msg.sender, "ReleaseEscrow: Only contract owners can release escrow");
        require (deposits[_guid].sender != address(0), "ReleaseEscrow: Escrow Deposit not found");


        Deposit memory _dep = deposits[_guid];
        require(_dep.sender != address(0), "ReleaseEscrow: Sender address is empty");
        require(_dep.receiver != address(0), "ReleaseEscrow: Receiver address is empty");

        if(_isRefund) {
            _dep.sender.transfer(_dep.amount);
            emit claimRefund(_dep.sender, _dep.amount);
        } else {
            _dep.receiver.transfer(_dep.amount);
            emit escrowReleased(_dep.sender, _dep.receiver, _dep.amount);
        }
        
        delete deposits[_guid];
    }
    
}
